var document = stm_document;

if (document) {
  var currentIterationState = signingRoutes.getState(document);
  if (currentIterationState != "NEW") {
    var lastIteration = signingRoutes.getLastIteration(document);
    if (lastIteration) {
       signingRoutes.createIterationFromPrevious(document);
    }
  }
  var variables = base.getValueConverter().convertValueForJava({
      workflowDefinition: 'lecmSigningWorkflow-v2',
      documentRef: document.nodeRef
  });

  routeWorkflow.startWorkflow(variables);
}

