    if(childrenResolutions){
      children = childrenResolutions;
    }
    if(childrenErrands && childrenResolutions){
      children = childrenErrands.concat(childrenResolutions);
    }
  }
 
	var hasOnExecution = false;
	var docExecutor = doc.properties["lecm-errands:executor-assoc-ref"];
	if(children && children.length){
		for(var i =0; i<children.length; i++){

		  var childAuthor = null;
		  if(children[i].type == "{http://www.it.ru/logicECM/resolutions/1.0}document"){
			childAuthor = children[i].properties["lecm-resolutions:initiator-assoc-ref"];
		  }
		  if(children[i].type == "{http://www.it.ru/logicECM/errands/1.0}document"){
			childAuthor = children[i].properties["lecm-errands:initiator-assoc-ref"];
		  }
		  if(docExecutor && childAuthor && docExecutor == childAuthor){
			if (!statemachine.isFinal(children[i].nodeRef.toString())) {
				if(children[i].properties["lecm-statemachine:status"] == "На исполнении"){
				  hasOnExecution = true;			 			  			 
				  break;			  
				}  
			}  		
		  }    
		}
	}

	doc.properties["lecm-errands:transit-to-on-execution"] = hasOnExecution;
	doc.save();

  
  
}

