var document = stm_document;
if (document) {
    document.reset();
    var prevStatus = statemachine.getPreviousStatusName(document);
    if ((!prevStatus || prevStatus == "Черновик") && !regnumbers.isDocumentRegistered(document)) {
        var regNumberCode;
        var parentDoc = errands.getAdditionalDocument(document.nodeRef.toString());
        if (!parentDoc) {
            regNumberCode = "ERRAND_FREE_NUMBER";
        } else {
            regNumberCode = "ERRAND_ON_BASIS_NUMBER";
            var parentDocTypeShort = parentDoc.typeShort;
            if (parentDocTypeShort == "lecm-errands:document"){
                regNumberCode = "ERRAND_ON_BASIS_NUMBER";
            } else if (parentDocTypeShort == "lecm-incoming:document"){
                regNumberCode = "ERRAND_ON_INCOMING_BASIS_NUMBER";
            } else if (parentDocTypeShort == "lecm-outgoing:document"){
                regNumberCode = "ERRAND_ON_OUTGOING_BASIS_NUMBER";
            } else if (parentDocTypeShort == "lecm-internal:document"){
                regNumberCode = "ERRAND_ON_INTERNAL_BASIS_NUMBER";
            } else if (parentDocTypeShort == "lecm-ord:document"){
                regNumberCode = "ERRAND_ON_ORD_BASIS_NUMBER";
            } else if (parentDocTypeShort == "lecm-nd:document"){
                regNumberCode = "ERRAND_ON_ND_BASIS_NUMBER";
            } else if (parentDocTypeShort == "lecm-protocol:document"){
                regNumberCode = "ERRAND_ON_PROTOCOL_BASIS_NUMBER";
            } else if (parentDocTypeShort == "lecm-contract:document"){
                regNumberCode = "ERRAND_ON_CONTRACT_BASIS_NUMBER";
            } else if (parentDocTypeShort == "lecm-additional-document:additionalDocument"){
                regNumberCode = "ERRAND_ON_CONTRACT_DOCUMENT_BASIS_NUMBER";
            }
            if (document.properties["lecm-errands:child-index-counter"] == 0) {
                var childAssoc = parentDoc.sourceAssocs["lecm-errands:additional-document-assoc"];
                if (childAssoc && childAssoc.length) {
                   var childIndex = childAssoc.filter(function(child){
                        return !statemachine.isDraft(child);
                   }).length;
                   document.properties["lecm-errands:child-index-counter"] = childIndex;
                   document.save();
                }
            }
        }
        regnumbers.registerDocument(document, regNumberCode);

        var initiator = null;
        var initiatorAssoc = document.assocs["lecm-errands:initiator-assoc"];
        if (initiatorAssoc && initiatorAssoc.length == 1) {
            initiator = initiatorAssoc[0];
            statemachine.grandDynamicRoleForEmployee(document, initiator, "BR_INITIATOR", task);
            documentMembers.addMember(document, initiator, "ERRANDS_READER", true);
        }
        var controllerAssoc = document.assocs["lecm-errands:controller-assoc"];
        if (controllerAssoc && controllerAssoc.length == 1) {
            var controller = controllerAssoc[0];
            statemachine.grandDynamicRoleForEmployee(document, controller, "ERRANDS_CONTROLLER", task);
            documentMembers.addMember(document, controller, "ERRANDS_READER", true);

            if (initiator) {
                notifications.sendNotificationFromCurrentUser({
                    recipients: [controller],
                    templateCode: 'ERRANDS_CONTROLLER_ASSIGN',
                    templateConfig: {
                        mainObject: document,
                        eventExecutor: initiator
                    }
                });
            }
        }

        var executorAssoc = document.assocs["lecm-errands:executor-assoc"];
        if (executorAssoc && executorAssoc.length == 1) {
            var executor = executorAssoc[0];
            statemachine.grandDynamicRoleForEmployee(document, executor, "ERRANDS_EXECUTOR", task);
            if (initiator) {
                notifications.sendNotificationFromCurrentUser({
                    recipients: [executor],
                    templateCode: 'ERRANDS_DIRECTED_BY_INITIATOR',
                    templateConfig: {
                        mainObject: document,
                        eventExecutor: initiator
                    }
                });
            }
            documentMembers.addMember(document, executor, "ERRANDS_READER", true);
        }

        var soExecutors = document.assocs["lecm-errands:coexecutors-assoc"];
        if (soExecutors) {
            for (var i = 0; i < soExecutors.length; i++) {
                statemachine.grandDynamicRoleForEmployee(document, soExecutors[i], "ERRANDS_COEXECUTOR", task);
                documentMembers.addMember(document, soExecutors[i], "ERRANDS_READER", true);
            }
            if (initiator) {
                notifications.sendNotificationFromCurrentUser({
                    recipients: soExecutors,
                    templateCode: 'ERRANDS_CO_EXECUTOR',
                    templateConfig: {
                        mainObject: document,
                        eventExecutor: initiator
                    }
                });
            }

        }
    }
}

