var document = bpm_package.children[0];
if (document) {
	var limitationDateRadio = document.properties["lecm-errands:limitation-date-radio"];
	var limitationDateDays = document.properties["lecm-errands:limitation-date-days"];
	var limitationDateDaysType = document.properties["lecm-errands:limitation-date-type"];
	var limitationDate = document.properties["lecm-errands:limitation-date"];
	var dateString = "";
	if (limitationDateRadio) {
		if (limitationDateRadio == "DATE" && limitationDate) {
			var day = utils.pad(limitationDate.getDate(), 2);
			var month = utils.pad(limitationDate.getMonth() + 1, 2);
			var year = utils.pad(limitationDate.getFullYear(), 4);
			dateString = day + "." + month + "." + year;
		} else if (limitationDateRadio == "DAYS") {
			if (limitationDateDays && limitationDateDaysType) {
				if (limitationDateDaysType == "WORK") {
					dateString = limitationDateDays + " р.д.";
				} else if (limitationDateDaysType == "CALENDAR") {
					dateString = limitationDateDays + " к.д.";
				}
			}
		} else if (limitationDateRadio == "LIMITLESS") {
			dateString = "Без срока";
		}
	}

	document.properties["lecm-errands:limitation-date-text"] = dateString;
	document.save();
}
