var doc = bpm_package.children[0];
if(doc){
  var children = [];
  var childrenErrands = errands.getChildErrands(doc.nodeRef.toString());
  var childrenResolutions = documentConnection.getConnectedDocuments(doc,'onBasis',"lecm-resolutions:document");
 				
  if(childrenErrands || childrenResolutions){
    if(childrenErrands){
    	children = childrenErrands;
    }
    if(childrenResolutions){
      children = childrenResolutions;
    }
    if(childrenErrands && childrenResolutions){
      children = childrenErrands.concat(childrenResolutions);
    }
  }
 
	var hasOnExecution = false;
	var docExecutor = doc.properties["lecm-errands:executor-assoc-ref"];
	if(children && children.length){
		for(var i =0; i<children.length; i++){

		  var childAuthor = null;
		  if(children[i].type == "{http://www.it.ru/logicECM/resolutions/1.0}document"){
			childAuthor = children[i].properties["lecm-resolutions:initiator-assoc-ref"];
		  }
		  if(children[i].type == "{http://www.it.ru/logicECM/errands/1.0}document"){
			childAuthor = children[i].properties["lecm-errands:initiator-assoc-ref"];
		  }
		  if(docExecutor && childAuthor && docExecutor == childAuthor){
			if (!statemachine.isFinal(children[i].nodeRef.toString())) {
				if(children[i].properties["lecm-statemachine:status"] == "На исполнении"){
				  hasOnExecution = true;			 			  			 
				  break;			  
				}  
			}  		
		  }    
		}
	}

	doc.properties["lecm-errands:transit-to-on-execution"] = hasOnExecution;
	doc.save();

  
  
}

var document = stm_document;
if (document) {
    var prevStatus = statemachine.getPreviousStatusName(document);
    if (!prevStatus || prevStatus == "Черновик") {
        if (!regnumbers.isDocumentRegistered(document)) {
            var regNumberCode;
            var parentDoc = errands.getAdditionalDocument(document.nodeRef.toString());
            if (!parentDoc) {
                regNumberCode = "ERRAND_FREE_NUMBER";
            } else {
                regNumberCode = "ERRAND_ON_BASIS_NUMBER";
            }
            regnumbers.registerDocument(document, regNumberCode);
            regnumbers.setDocumentNumber(regNumberCode, document, "lecm-errands:number");
        }
        var initiator = null;
        var initiatorAssoc = document.assocs["lecm-errands:initiator-assoc"];
        if (initiatorAssoc && initiatorAssoc.length == 1) {
            initiator = initiatorAssoc[0];
            statemachine.grandDynamicRoleForEmployee(document, initiator, "BR_INITIATOR", task);
            documentMembers.addMember(document, initiator, "ERRANDS_READER");
        }
        var controllerAssoc = document.assocs["lecm-errands:controller-assoc"];
        if (controllerAssoc && controllerAssoc.length == 1) {
            var controller = controllerAssoc[0];
            statemachine.grandDynamicRoleForEmployee(document, controller, "ERRANDS_CONTROLLER", task);
            documentMembers.addMember(document, controller, "ERRANDS_READER");

            if (initiator) {
                notifications.sendNotificationFromCurrentUser({
                    recipients: [controller],
                    templateCode: 'ERRANDS_CONTROLLER_ASSIGN',
                    templateConfig: {
                        mainObject: document,
                        eventExecutor: initiator
                    }
                });
            }
        }

        var executorAssoc = document.assocs["lecm-errands:executor-assoc"];
        if (executorAssoc && executorAssoc.length == 1) {
            var executor = executorAssoc[0];
            statemachine.grandDynamicRoleForEmployee(document, executor, "ERRANDS_EXECUTOR", task);
            if (initiator) {
                notifications.sendNotificationFromCurrentUser({
                    recipients: [executor],
                    templateCode: 'ERRANDS_DIRECTED_BY_INITIATOR',
                    templateConfig: {
                        mainObject: document,
                        eventExecutor: initiator
                    }
                });
            }
            documentMembers.addMember(document, executor, "ERRANDS_READER");
        }

        var soExecutors = document.assocs["lecm-errands:coexecutors-assoc"];
        if (soExecutors) {
            for (var i = 0; i < soExecutors.length; i++) {
                statemachine.grandDynamicRoleForEmployee(document, soExecutors[i], "ERRANDS_COEXECUTOR", task);
                documentMembers.addMember(document, soExecutors[i], "ERRANDS_READER");
            }
            if (initiator) {
                notifications.sendNotificationFromCurrentUser({
                    recipients: soExecutors,
                    templateCode: 'ERRANDS_CO_EXECUTOR',
                    templateConfig: {
                        mainObject: document,
                        eventExecutor: initiator
                    }
                });
            }

        }
    }
}

