(function() {
var errand = stm_document,
    isExpired = !!errand.properties['lecm-errands:is-expired'],
    initiators = errand.assocs['lecm-errands:initiator-assoc'],
    executors =  errand.assocs['lecm-errands:executor-assoc'],
    controllers = errand.assocs['lecm-errands:controller-assoc'],
    recipients = initiators.concat(executors);

    if (controllers && controllers.length) {
        Array.prototype.push.apply(recipients, controllers);
    }
  	if (isExpired) {
      notifications.sendNotification({
          recipients: recipients,
          templateCode: 'ERRANDS_EXPIRED',
          templateConfig: {
              mainObject: errand
          },
          dontCheckAccessToObject: true
      });
  	}
})();

var document = bpm_package.children[0];
if (document) {
  var prevStatus = statemachine.getPreviousStatusName(document);
  if(!prevStatus || prevStatus == "Черновик"){ 
	
    var limitationDateRadio = document.properties["lecm-errands:limitation-date-radio"];
    var limitationDateDays = document.properties["lecm-errands:limitation-date-days"];
    var limitationDateDaysType = document.properties["lecm-errands:limitation-date-type"];
    var limitationDate = document.properties["lecm-errands:limitation-date"];
    var dateString;
    if(limitationDateRadio) {
        if(limitationDateRadio == "LIMITLESS"){
            limitationDate = null;
            dateString = "Без срока";
          	document.properties["lecm-errands:limitation-date"]=limitationDate;
        }else if( limitationDateRadio == "DATE" || limitationDateRadio == "DAYS"){
            if( limitationDateRadio == "DAYS" ){
                if(limitationDateDays && limitationDateDaysType) {
                    var date = new Date();
                    if (limitationDateDaysType == "WORK") {
                        limitationDate = workCalendar.getNextWorkingDateByDays(utils.toISO8601(date), limitationDateDays);
                    } else if (limitationDateDaysType == "CALENDAR") {
                        limitationDate = date;
                        limitationDate.setDate(limitationDate.getDate() + limitationDateDays);
                    }
                }
            }
            if(limitationDate){
                var jsLimitDate = new Date(limitationDate.getTime());
                var dateFormat = new Packages.java.text.SimpleDateFormat("dd.MM.yyyy");
                dateString = dateFormat.format(jsLimitDate).toString();
              	document.properties["lecm-errands:limitation-date"] = jsLimitDate;
              	var date = new Date(document.properties["lecm-errands:wait-for-execution-date"]);
              	var limitDays = Math.round((jsLimitDate - date) / (1000 * 60 * 60 * 24));
              	date.setDate(date.getDate()+ Math.round(limitDays/2));
              	document.properties["lecm-errands:half-limit-date"] = date;
              	
              	var shortLimitDays = notifications.getSettingsShortLimitDays();
              	if(limitDays <= shortLimitDays){
                  document.properties["lecm-errands:is-limit-short"] = true;
                }
           	
            }
        }
        if(dateString) {
            document.properties["lecm-errands:limitation-date-text"] = dateString;
        }
     
    }
    document.save();
  }
}

