var document = stm_document;
if(document){
  var prevStatus = statemachine.getPreviousStatusName(document);
  if(!prevStatus || prevStatus == "Черновик"){
    if (document.properties["lecm-errands:number"] == null || document.properties["lecm-errands:number"] == "Не присвоено") {
        regnumbers.setDocumentNumber("ERRAND_NUMBER", document, "lecm-errands:number");
    }

    var controllerAssoc = document.assocs["lecm-errands:controller-assoc"];
    if (controllerAssoc != null && controllerAssoc.length == 1) {
       var controller = controllerAssoc[0];
       lecmPermission.grantDynamicRole(document, controller , "ERRANDS_CONTROLLER", "LECM_BASIC_PG_Reader");
    }

    var initiator = null;
    var initiatorAssoc = document.assocs["lecm-errands:initiator-assoc"];
    if (initiatorAssoc != null && initiatorAssoc.length == 1) {
        initiator = initiatorAssoc[0];
    }

    var executorAssoc = document.assocs["lecm-errands:executor-assoc"];
    if (executorAssoc != null && executorAssoc.length == 1) {
        var executor = executorAssoc[0];

        lecmPermission.grantDynamicRole(document, executor, "ERRANDS_EXECUTOR", "LECM_BASIC_PG_ActionPerformer");

        if (initiator != null) {
            notifications.sendNotificationFromCurrentUser({
                recipients: [executor],
                templateCode: 'ERRANDS_DIRECTED_BY_INITIATOR',
                templateConfig: {
                    mainObject: document,
                    eventExecutor: initiator
                }
            });
        }
    }

    var soExecutors = document.assocs["lecm-errands:coexecutors-assoc"];
    if (soExecutors != null) {
        for (var i = 0; i < soExecutors.length; i++) {
            documentMembers.addMember(document, soExecutors[i], "ERRANDS_READER");
          statemachine.grandDynamicRoleForEmployee(document, soExecutors[i], "ERRANDS_COEXECUTOR", task);
        }

        if (initiator != null) {
            notifications.sendNotificationFromCurrentUser({
               recipients: soExecutors,
               templateCode: 'ERRANDS_CO_EXECUTOR',
               templateConfig: {
                   mainObject: document,
                   eventExecutor: initiator
               }
            });
        }
    }
  }
}

