var doc = stm_document;
if (doc) {
  var parentDoc = errands.getAdditionalDocument(doc.nodeRef.toString());
  if (parentDoc && parentDoc.type == "{http://www.it.ru/logicECM/errands/1.0}document") {
        var children = errands.getChildErrands(parentDoc.nodeRef.toString());
        if (children && children.length) {
            var count1 = 0;
          	var count2 = 0;
          	var count3 = 0; 
            children.forEach(function (child) {
                if (statemachine.isFinal(child.nodeRef.toString())) {
                    count1++;
                    if(child.properties["lecm-errands:initiator-assoc-ref"] == parentDoc.properties["lecm-errands:executor-assoc-ref"]){
                    	count2++;
                    }
                    
                 }
               
                if(child.properties["lecm-errands:initiator-assoc-ref"] == parentDoc.properties["lecm-errands:executor-assoc-ref"]){
                  count3++;
                }
            });
            if (count1 == children.length) {
              parentDoc.properties["lecm-errands:is-all-child-on-final"] = true;              
            }
          if(count2 == count3){
            parentDoc.properties["lecm-errands:has-child-executing-by-executor"]=false;
          }
          	parentDoc.save();
            
        }
    }
}

