var document = bpm_package.children[0];
var currentUser = orgstructure.getCurrentEmployee();
if (document) {
    var currentReport = null;
    var reportsTable = document.assocs["lecm-errands-ts:execution-reports-assoc"][0];
    var executionReports = documentTables.getTableDataRows(reportsTable.nodeRef.toString());
    if (executionReports && executionReports.length){
        var onControlReports = executionReports.filter(function(report) {
            return report.properties["lecm-errands-ts:execution-report-status"] == "ONCONTROL";
        });
         if (onControlReports && onControlReports.length) {
            currentReport = onControlReports[onControlReports.length - 1];
        }
    }
    if (currentReport) {
        currentReport.properties["lecm-errands-ts:execution-report-decline-reason"] = lecmErrandWf_approverDeclineReason;
        currentReport.properties["lecm-errands-ts:execution-report-status"] = "DECLINE";
        currentReport.save();
        var executor = null;
        var executorAssoc = document.assocs["lecm-errands:executor-assoc"];
        if (executorAssoc && executorAssoc.length == 1) {
            executor = executorAssoc[0];
        }

        notifications.sendNotificationFromCurrentUser({
            recipients: [executor],
            templateCode: 'ERRANDS_DECLINE_EXEC',
            templateConfig: {
                mainObject: document,
                eventExecutor: currentUser,
                reason: lecmErrandWf_approverDeclineReason
            }
        });
    }
}
