														return false;
													}
												}
												return true;
											});
											removedExecutors = oldCoexecutors.filter(function(element){
												for (var i = 0; i < newCoexecutors.length; i++) {
													if(element.nodeRef.equals(newCoexecutors[i].nodeRef)){
														return false;
													}
												}
												return true;
											});
									} else {
										finalNewCoexecutors = newCoexecutors;
									}
								} else {
