										finalNewCoexecutors.forEach(function(coexecutor){
											document.createAssociation(coexecutor, "lecm-errands:coexecutor-assoc");
											statemachine.grandDynamicRoleForEmployee(document, coexecutor, "ERRANDS_COEXECUTOR");

										});
