property.value:
var document = bpm_package.children[0];
if (document.properties["lecm-errands:number"] == null || document.properties["lecm-errands:number"] == "Не присвоено") {
	regnumbers.setDocumentNumber("ERRAND_NUMBER", document, "lecm-errands:number");
}

var initiator = null;
var initiatorAssoc = document.assocs["lecm-errands:initiator-assoc"];
if (initiatorAssoc != null && initiatorAssoc.length == 1) {
	initiator = initiatorAssoc[0];
}

var executorAssoc = document.assocs["lecm-errands:executor-assoc"];
if (executorAssoc != null && executorAssoc.length == 1) {
	var executor = executorAssoc[0];

	statemachine.grandDynamicRoleForEmployee(document, executor, "ERRANDS_EXECUTOR");

	if (initiator != null) {
		var notificationText = "";
		notificationText += documentScript.wrapperLink(initiator, "{lecm-errands:initiator-assoc/lecm-orgstr:employee-short-name}");
		notificationText += " направил(а) вам поручение ";
		notificationText += documentScript.wrapperDocumentLink(document, "{lecm-errands:title}");
		notificationText += " № {lecm-errands:number}";

		var recipients = [];
		recipients.push(executor);
		notifications.sendNotificationFromCurrentUser(recipients, notificationText, document);
	}
}

var soExecutors = document.assocs["lecm-errands:coexecutors-assoc"];
if (soExecutors != null) {
	for (var i = 0; i < soExecutors.length; i++) {
		documentMembers.addMember(document, soExecutors[i], "ERRANDS_READER");
	}

	if (initiator != null) {
		notificationText = "";
		notificationText += documentScript.wrapperLink(initiator, "{lecm-errands:initiator-assoc/lecm-orgstr:employee-short-name}");
		notificationText += " назначил вас соисполнителем по поручению ";
		notificationText += documentScript.wrapperDocumentLink(document, "{lecm-errands:title}");
		notificationText += " № {lecm-errands:number}";

		notifications.sendNotificationFromCurrentUser(soExecutors, notificationText, document);
	}
}
