property.value:
(function(){
var document = bpm_package.children[0];
if (document) {
    lecmPermission.pushAuthentication();
    lecmPermission.setRunAsUserSystem();
    try {
        var resolutions = document.sourceAssocs["lecm-resolutions:base-document-assoc"];
		var errands = document.sourceAssocs["lecm-errands:base-assoc"];
        var hasResolutionOnExecution = resolutions && resolutions.some(function(resolution) {
                return resolution.properties["lecm-statemachine:status"] == "На исполнении";
            });
		var hasErrandOnExecution = errands && errands.some(function(errand) {
				var statusProperty = errand.properties["lecm-statemachine:status"];
          		return statusProperty == "Ожидает исполнения" || statusProperty == "На периодическом исполнении";
			});

        if (hasResolutionOnExecution || hasErrandOnExecution) {
            document.properties["lecm-incoming:auto-transition-to-execution"] = true;
            document.save();
        }
        edsDocument.resetChildChangeSignal(document);
    } finally {
        lecmPermission.popAuthentication();
    }
}
})();

(function(){
    var incoming = bpm_package.children[0];

    if (orgstructure.isEmployeeHasBusinessRole(orgstructure.getCurrentEmployee(), "DA_REGISTRARS", true, true) && !regnumbers.isDocumentRegistered(incoming)) {
        regnumbers.registerDocument(incoming, "INCOMING_NUMBER");
        incoming.save();

        var sender = null;
        var senderAssoc = incoming.assocs["lecm-incoming:sender-assoc"];
        if (senderAssoc && senderAssoc.length == 1) {
            sender = senderAssoc[0];
        }

        if (sender) {
            var logText;
            var email = sender.properties["lecm-contractor:email"];
            var contractorName = sender.properties["lecm-contractor:fullname"];
            if (email && email.length) {
                var outgoingNumber = incoming.properties["lecm-incoming:outgoing-number"];
                var outgoingDate = substitude.formatNodeTitle(incoming, "{lecm-incoming:outgoing-date?dd.MM.yyyy}");
                var message = "Добрый день! <br>";
                message += "Ваше письмо ";
                message += incoming.properties["lecm-document:title"];
                message += " № ";
                message += outgoingNumber ? outgoingNumber + " от ": "б/н от <b>";
                message += outgoingDate && outgoingDate.length()? outgoingDate : "дата не указана";
                message += "</b>, поступившее ";
                message += substitude.formatNodeTitle(incoming, "{cm:created?dd.MM.yyyy}");
                message += ", зарегистрировано ";
                message += substitude.formatNodeTitle(incoming, "{lecm-document-aspects:reg-data-date?dd.MM.yyyy}");
                message += " под номером <b>";
                message += documentScript.getDocumentRegNumber(incoming);
                message += "</b>.";

                notificationsEmail.sendEmail("", message, email);

                logText = "Корреспонденту " + contractorName + " направлено уведомление о получении документа";
            } else {
                logText = "Корреспонденту " + contractorName + " не было направлено уведомление по причине отсутствия адреса электронной почты";
            }
            businessJournal.log(incoming.nodeRef.toString(), "EDIT", logText, []);
        }
    }
})();

(function(){
    var incoming = bpm_package.children[0];
		recipients = [];
  		recipientsAssocs = incoming.associations["lecm-incoming:recipient-assoc"];
  	if (recipientsAssocs) {
    	recipientsAssocs.forEach(function (recipient) {
      	if (recipient.typeShort == "lecm-orgstr:employee") {
        	recipients.push(recipient);
      	} else if (recipient.typeShort == "lecm-orgstr:organization-unit") {
        	boss = orgstructure.findUnitBoss(recipient.nodeRef.toString());
        	if (boss) {
          		recipients.push(boss);
        	}
      	}
    	});
  	}
	if (recipients && recipients.length) {
      	for each (var recipient in recipients) {
          	if (orgstructure.isEmployeeHasBusinessRole(recipient, "DA_RECIPIENT_DYN", true, true)) {
      			documentMembers.addMemberWithoutCheckPermission(incoming, recipient, "LECM_BASIC_PG_Reader", true);
            }
      	}
    }
})();

(function(){
  var recipients = [];
  var recipientsAssocs = stm_document.associations["lecm-incoming:recipient-assoc"];
  if (recipientsAssocs) {
    recipientsAssocs.forEach(function (recipient) {
      if (recipient.typeShort == "lecm-orgstr:employee") {
        recipients.push(recipient);
        statemachine.grandDynamicRoleForEmployee(stm_document, recipient, "DA_RECIPIENT_DYN", task);
      } else if (recipient.typeShort == "lecm-orgstr:organization-unit") {
        boss = orgstructure.findUnitBoss(recipient.nodeRef.toString());
        if (boss) {
          recipients.push(boss);
          statemachine.grandDynamicRoleForEmployee(stm_document, boss, "DA_RECIPIENT_DYN", task);
        }
      }
    });
  }
})();

(function(){
  var document = bpm_package.children[0];
  document.properties["lecm-incoming:auto-transition-to-execution"] = false;
  document.properties["lecm-incoming:auto-transition-to-registered"] = false;
  document.save();
})();

(function(){
  var recipients = [];
  var recipientsAssocs = stm_document.associations["lecm-incoming:recipient-assoc"];
  if (recipientsAssocs) {
    recipientsAssocs.forEach(function (recipient) {
      if (recipient.typeShort == "lecm-orgstr:employee") {
        recipients.push(recipient);
        statemachine.grandDynamicRoleForEmployee(stm_document, recipient, "DA_RECIPIENT_DYN", task);
      } else if (recipient.typeShort == "lecm-orgstr:organization-unit") {
        boss = orgstructure.findUnitBoss(recipient.nodeRef.toString());
        if (boss) {
          recipients.push(boss);
          statemachine.grandDynamicRoleForEmployee(stm_document, boss, "DA_RECIPIENT_DYN", task);
        }
      }
    });
  }

  notifications.sendNotificationFromCurrentUser({
    recipients: recipients,
    templateCode: 'INCOMING_SEND_TO_RECIPIENT',
    templateConfig: {
      mainObject: stm_document
    },
    dontCheckAccessToObject: true
  });
})();

