        currentEmployee = orgstructure.getCurrentEmployee();
    lecmPermission.grantDynamicRole(incoming, currentEmployee, "DA_INITIATOR_DYN", "LECM_BASIC_PG_Owner");
})();

(function(){
    var doc = bpm_package.children[0];

    var recipient = doc.associations["lecm-incoming:recipient-assoc"];
    statemachine.grandDynamicRoleForEmployee(doc, recipient[0], "DA_RECIPIENT_DYN", task);

    var notificationText = "Вам направлен новый документ ";
    notificationText += documentScript.wrapperDocumentLink(doc,"{lecm-document:title}");

    notifications.sendNotificationFromCurrentUser(recipient,notificationText, doc, true);
})();

(function(){
    var incoming = bpm_package.children[0];
    var recipient = incoming.assocs["lecm-incoming:recipient-assoc"];
    if (recipient != null && recipient.length == 1) {
        lecmPermission.revokeDynamicRole(incoming, recipient[0], "DA_RECIPIENT_DYN");
    }
    var curators =  orgstructure.getEmployeesByBusinessRoleId("DA_REGISTRARS", true);
    var notificationText = documentScript.wrapperLink(recipient[0].nodeRef,"{lecm-incoming:recipient-assoc/lecm-orgstr:employee-short-name}");
    notificationText += " просит зарегистрировать ";
    var docTitle = substitude.formatNodeTitle(incoming, "входящий документ {lecm-incoming:document-type-assoc/cm:name} от {lecm-incoming:sender-assoc/lecm-contractor:fullname}");
    notificationText += documentScript.wrapperDocumentLink(incoming, docTitle);
    notifications.sendNotificationFromCurrentUser(curators, notificationText, incoming);

	var currentUser =  orgstructure.getCurrentEmployee();
    var logText = documentScript.wrapperLink(currentUser, currentUser.properties["lecm-orgstr:employee-short-name"]) + " вернул(а) документ ";
    logText += documentScript.wrapperDocumentLink(incoming , "№ {~REGNUM} от {~REGDATE}");


	businessJournal.log(incoming.nodeRef.toString(), "RETURN_DOCUMENT", substitude.formatNodeTitle(incoming.nodeRef.toString(), logText), []);
})();

(function(){
    var incoming = bpm_package.children[0];

    if (orgstructure.isEmployeeHasBusinessRole(orgstructure.getCurrentEmployee(), "DA_REGISTRARS", true, true) && !regnumbers.isDocumentRegistered(incoming)) {
        regnumbers.registerDocument(incoming, "INCOMING_NUMBER");
        incoming.save();

        var addressee = null;
        var addresseeAssoc = incoming.assocs["lecm-incoming:addressee-assoc"];
        if (addresseeAssoc != null && addresseeAssoc.length == 1) {
            addressee = addresseeAssoc[0];
        }

        if (addressee != null) {
            var addresseeFIO = "";
            addresseeFIO += addressee.properties["lecm-representative:surname"] + " ";
            addresseeFIO += addressee.properties["lecm-representative:firstname"] + " ";
            addresseeFIO += addressee.properties["lecm-representative:middlename"];

            var logText;

            var email = addressee.properties["lecm-representative:email"];
            if (email != null && email.length > 0) {
                var message = "Уважаемый(ая) <b>";
                message += addresseeFIO;
                message += "</b>!<br/>"

                message += "Ваше письмо ";
                message += incoming.properties["lecm-document:title"];
                message += " от <b>";
                message += substitude.formatNodeTitle(incoming, "{cm:created?dd.MM.yyyy}");
                message += "</b> зарегистрировано под номером <b>";
                message += documentScript.getDocumentRegNumber(incoming);
                message += "</b>.";

                notificationsEmail.sendEmail("", message, email);

                logText = "Адресанту " + addresseeFIO + " направлено уведомление о получении документа";
            } else {
                logText = "Адресанту " + addresseeFIO + " не было направлено уведомление по причине отсутствия адреса электронной почты";
            }
            businessJournal.log(incoming.nodeRef.toString(), "EDIT", logText, []);
        }
    }
})();

(function(){
    var incoming = bpm_package.children[0];
    var recipient = incoming.assocs["lecm-incoming:recipient-assoc"];
    if (recipient != null && recipient.length == 1) {
        lecmPermission.revokeDynamicRole(incoming, recipient[0], "DA_RECIPIENT_DYN");
        incoming.removeAssociation(recipient[0], "lecm-eds-document:recipients-assoc");
    }
    var currentUser =  orgstructure.getCurrentEmployee();
    incoming.removeAssociation(currentUser, "lecm-eds-document:recipients-assoc");
    lecmPermission.revokeDynamicRole(incoming, currentUser, "DA_RECIPIENT_DYN");
    var curators =  orgstructure.getEmployeesByBusinessRoleId("DA_REGISTRARS", true);
    var notificationText = documentScript.wrapperLink(currentUser, currentUser.properties["lecm-orgstr:employee-short-name"]) + " вернул(а) документ ";
    notificationText += documentScript.wrapperDocumentLink(incoming , "№ {~REGNUM} от {~REGDATE}");
    if (sendToExecution != null) {
      var sendToExecutionUser = search.findNode(sendToExecution);
      if (sendToExecutionUser != null) {
          notificationText += " сотруднику " + documentScript.wrapperLink(sendToExecutionUser, sendToExecutionUser.properties["lecm-orgstr:employee-short-name"]);
      }
    }

    notifications.sendNotificationFromCurrentUser(curators, notificationText, incoming);

  	businessJournal.log(incoming.nodeRef.toString(), "RETURN_DOCUMENT", substitude.formatNodeTitle(incoming.nodeRef.toString(), notificationText), []);
})();

