/* направить на регистрацию */
(function () {
    var i,
            internal = stm_document, /* исходящий документ */
            registrars, recipients = [],
            notificationText = 'Документ ' + documentScript.wrapperDocumentLink(internal, '{lecm-document:present-string}') + ' поступил Вам на регистрацию',
            registratorAssocs = internal.assocs['lecm-document-aspects:registrator-assoc'];

    if (registratorAssocs && registratorAssocs.length) {
        registrars = [delegation.getEffectiveEmployee(registratorAssocs[0], 'DA_REGISTRARS')];
    } else {
        registrars = edsGlobalSettings.getRegistrars('DA_REGISTRARS', internal);
        /* получение списка регистраторов по документу */
    }
    /* раздаем права регистраторам */
    for (i = 0; i < registrars.length; ++i) {
        statemachine.grandDynamicRoleForEmployee(internal, registrars[i], 'DA_REGISTRAR_DYN', task);
    }

    /* уведомление */
    notifications.sendNotificationFromCurrentUser({
        recipients: registrars,
        templateCode: 'INTERNAL_REGISTER',
        templateConfig: {
            mainObject: stm_document
        },
        dontCheckAccessToObject: true
    });
})();

