})();

(function(){
    var senders = documentEvent.getEventSenders(stm_document);
    for each (var sender in senders) {
    if (sender.getTypeShort() == "lecm-internal-table-structure:answer") {
        var finalize = true;
        var answers = stm_document.assocs["lecm-internal-table-structure:answers-assoc"][0].children;
        for each (var answer in answers) {
            logger.error(answer.properties["lecm-internal-table-structure:answer-complete"]);
            finalize = finalize && answer.properties["lecm-internal-table-structure:answer-complete"];
        }
        if (finalize) {
            stm_document.properties["lecm-internal:all-answers-complete"] = true;
            stm_document.save();
        }
    } else {
        var answer = sender.sourceAssocs["lecm-internal-table-structure:answer-document-assoc"][0];
        if (answer != null && sender.properties["lecm-statemachine:status"] == "Направлен") {
            answer.properties["lecm-internal-table-structure:answer-complete"] = true;
            answer.properties["lecm-internal-table-structure:answer-details"] = "!html " + documentScript.wrapperAttribute(sender, sender.properties["lecm-document:title"]);
            answer.save();
        }
    }
    documentEvent.removeEventSender(stm_document, sender);
}
})();

(function(){
	var units = [];

    var author = stm_document.assocs["lecm-document:author-assoc"][0];
    var primaryUnit = orgstructure.getPrimaryOrgUnit(author);
	if(primaryUnit) {
        units.push(primaryUnit)
    }

    var recipients = stm_document.assocs["lecm-eds-document:recipients-assoc"];
    var additionalUnits = [];
    for each (var recipient in recipients) {
        var unit = orgstructure.getPrimaryOrgUnit(recipient);
        units.push(unit);
    }

    operativeStorage.moveToNomenclatureCase(stm_document, units);

    /* Выставляем права на ответы */
    var answersTable = stm_document.assocs["lecm-internal-table-structure:answers-assoc"][0];
    var answers = answersTable.getChildren();
    for each (var answer in answers) {
        var employee = answer.assocs["lecm-internal-table-structure:employee-assoc"][0];
        var unit = orgstructure.getPrimaryOrgUnit(employee);
        if (primaryUnit != null) {
            var initiatorAuthority = orgstructure.getOrgstructureUnitAuthority(primaryUnit, false); 
            answer.setPermission("LECM_BASIC_PG_Reader", initiatorAuthority);
        }
        if (unit != null) {
            var recipientAuthority = orgstructure.getOrgstructureUnitAuthority(unit, false);
            answer.setPermission("LECM_BASIC_PG_Reader", recipientAuthority);
        }
    }
})();

(function(){
    if (statemachine.getPreviousStatusName(stm_document) == "Проект") {
        var currentEmployee = orgstructure.getCurrentEmployee();

        var resultList = signingWorkflow.createEmptyResultList(bpm_package, "Внутренний");
        signingWorkflow.createResultItem(resultList, currentEmployee);

        var taskDecision = signingWorkflow.getSignedDecision(currentEmployee);
        signingWorkflow.logDecision(resultList, taskDecision);

        signingWorkflow.deleteTempAssigneesList(execution);

        signingWorkflow.addSignBusinessJournalRecord(bpm_package, currentEmployee);
    }
})();

