/* отнять права у получателей */
(function(){
    var internal = stm_document;
    var recipientsAssoc = internal.assocs["lecm-eds-document:recipients-assoc"];
    var recipients = []; 
    if (recipientsAssoc != null) {
        for (var i = 0; i < recipientsAssoc.length; i++) {
            lecmPermission.revokeDynamicRole(internal, recipientsAssoc[i], 'DA_RECIPIENT_DYN');
        }
    }
})();
