var document = bpm_package.children[0];
if (document != null) {
  var membersAssoc = document.assocs["lecm-internal:temp-members-assoc"];
  if (membersAssoc != null) {
    for (var i = 0; i < membersAssoc.length; i++) {
      documentMembers.addMemberWithoutCheckPermission(document, membersAssoc[i], "LECM_BASIC_PG_Reader");
    }
  }

}

