    internal.save();
})();

var internal = stm_document;

var logText = "#initiator отправил(а) документ " + documentScript.wrapperDocumentLink(internal, "№ {~REGNUM}") + " на согласование";
logText = substitude.formatNodeTitle(internal.nodeRef.toString(), logText);
businessJournal.log(internal.nodeRef.toString(), "SEND_DOCUMENT", logText, []);

(function () {
	var currentIterationNode = routesService.getDocumentCurrentIteration(stm_document),
		stageItems,
		employees,
		recipients = [],
		i,
		documentLink,
		msg;
	if (currentIterationNode) {
		stageItems = search.query({
			query: 'TYPE:"lecmWorkflowRoutes:stageItem" AND PATH:"' + currentIterationNode.qnamePath + '//*"',
			language: 'fts-alfresco'
		});
		for (i in stageItems) {
			employees = stageItems[i].assocs['lecmWorkflowRoutes:stageItemEmployeeAssoc'];
			if (employees && employees.length) {
				recipients.push(employees[0].nodeRef.toString());
			}
		}
		documentLink = documentScript.wrapperDocumentLink(stm_document, '{lecm-document:present-string}');
		msg = 'Документ ' + documentLink + ' был возвращен на доработку автором документа.';
		notifications.sendNotification(recipients, msg, stm_document);
	}
})();

/* направить на регистрацию */
(function(){
    var i,
        internal = stm_document, /* исходящий документ */
        registrars = edsGlobalSettings.getRegistrars('DA_REGISTRARS', internal), /* получение списка регистраторов по документу */
        registrarsCount = registrars.length,
        recipients = [],
        notificationText = 'Документ ' + documentScript.wrapperDocumentLink(internal, '{lecm-document:present-string}') + ' поступил Вам на регистрацию';

    /* раздаем права регистраторам */
    for(i = 0; i < registrarsCount; ++i) {
        statemachine.grandDynamicRoleForEmployee(internal, registrars[i], 'DA_REGISTRAR_DYN', task);
        recipients.push(registrars[i].getNodeRef().toString());
    }

    /* уведомление */
    notifications.sendNotificationFromCurrentUser(recipients, notificationText, internal, true);
})();

var internal = stm_document;

var logText = "#initiator направил(а) документ " + documentScript.wrapperDocumentLink(internal, "№ {~REGNUM}") + " на регистрацию";
