(function(){
    var internal = stm_document;
    var recipientsAssoc = internal.assocs["lecm-eds-document:recipients-assoc"];
    var recipients = []; 
    if (recipientsAssoc != null) {
        for (var i = 0; i < recipientsAssoc.length; i++) {
            lecmPermission.revokeDynamicRole(internal, recipientsAssoc[i], 'DA_RECIPIENT_DYN');
        }
    }
})();

(function(){
    var senders = documentEvent.getEventSenders(stm_document);
    for each (var sender in senders) {
    if (sender.getTypeShort() == "lecm-internal-table-structure:answer") {
        var finalize = true;
        var answers = stm_document.assocs["lecm-internal-table-structure:answers-assoc"][0].children;
        for each (var answer in answers) {
            logger.error(answer.properties["lecm-internal-table-structure:answer-complete"]);
            finalize = finalize && answer.properties["lecm-internal-table-structure:answer-complete"];
        }
        if (finalize) {
            stm_document.properties["lecm-internal:all-answers-complete"] = true;
            stm_document.save();
        }
    } else {
        var answer = sender.sourceAssocs["lecm-internal-table-structure:answer-document-assoc"][0];
        if (answer != null && sender.properties["lecm-statemachine:status"] == "Направлен") {
            answer.properties["lecm-internal-table-structure:answer-complete"] = true;
            answer.properties["lecm-internal-table-structure:answer-details"] = "!html " + documentScript.wrapperAttribute(sender, sender.properties["lecm-document:title"]);
            answer.save();
        }
    }
    documentEvent.removeEventSender(stm_document, sender);
}
})();

(function(){
	var units = [];

    var author = stm_document.assocs["lecm-document:author-assoc"][0];
    var primaryUnit = orgstructure.getPrimaryOrgUnit(author);
	if(primaryUnit) {
        units.push(primaryUnit)
    }

    var recipients = stm_document.assocs["lecm-eds-document:recipients-assoc"];
    var additionalUnits = [];
    for each (var recipient in recipients) {
        var unit = orgstructure.getPrimaryOrgUnit(recipient);
        units.push(unit);
    }

    operativeStorage.moveToNomenclatureCase(stm_document, units);

    /* Выставляем права на ответы */
    var answersTable = stm_document.assocs["lecm-internal-table-structure:answers-assoc"][0];
    var answers = answersTable.getChildren();
    for each (var answer in answers) {
        var employee = answer.assocs["lecm-internal-table-structure:employee-assoc"][0];
        var unit = orgstructure.getPrimaryOrgUnit(employee);
        if (primaryUnit != null) {
            var initiatorAuthority = orgstructure.getOrgstructureUnitAuthority(primaryUnit, false); 
            answer.setPermission("LECM_BASIC_PG_Reader", initiatorAuthority);
        }
        if (unit != null) {
            var recipientAuthority = orgstructure.getOrgstructureUnitAuthority(unit, false);
            answer.setPermission("LECM_BASIC_PG_Reader", recipientAuthority);
        }
    }
})();

var document = bpm_package.children[0];
if (document) {
  var recipients = document.assocs["lecm-eds-document:recipients-assoc"];
  if (recipients) {
    for (var i = 0; i < recipients.length; i++) {
      var employee = recipients[i];
      if ("" + edsGlobalSettings.isHidePropsForRecipients() == "true") {
        lecmPermission.revokeDynamicRole(document, employee, "DA_RECIPIENT_DYN");
        lecmPermission.grantDynamicRole(document, employee, "DA_RECIPIENT_DYN", "LECM_BASIC_PG_ActionPerformer_Lite");
      }
    }
  }
}

(function() {
	var document = stm_document;
	if (document) {
		var isReservationRunning = document.properties['lecm-reservation-aspects:isReservationRunning'],
		documentRegnum = document.properties["lecm-document:regnum"],
		projectNumber = document.properties["lecm-document-aspects:reg-project-data-number"],
		presentString = document.properties["lecm-document:present-string"],
		presentStringWithProjectNumber = presentString.replace(documentRegnum, projectNumber),
        autoRegistration = document.assocs["lecm-eds-document:document-type-assoc"][0].properties["lecm-doc-dic-dt:auto-registration"];
        if (isReservationRunning) {
                statemachine.terminateWorkflowsByDefinition(document, 'regnumReservationExecution', null, null);
            }
		if (!autoRegistration) {
            var initiators = lecmPermission.getEmployeesByDynamicRole(document, "BR_INITIATOR");
            var eventInitiator = orgstructure.getCurrentEmployee();
            if (initiators && initiators.length) {
                notifications.sendNotificationFromCurrentUser({
                    recipients: initiators,
                    templateCode: 'EDS_DOCUMENT_REGISTERED',
                    templateConfig: {
                        mainObject: document,
                        initiator: eventInitiator,
                        presentStringWithProjectNumber: presentStringWithProjectNumber
                    }
                });
            }

            var registrationRequired = document.assocs["lecm-eds-document:document-type-assoc"][0].properties["lecm-doc-dic-dt:registration-required"];
	        if (registrationRequired) {
                var logText = "#initiator зарегистрировал документ " + documentScript.wrapperDocumentLink(document, presentStringWithProjectNumber) + ". Присвоен номер {~REGNUM} на дату {~REGDATE}.";
                logText = substitude.formatNodeTitle(document.nodeRef.toString(), logText);
                businessJournal.log(document.nodeRef.toString(), "REGISTRATION", logText, []);
            }
        }
	}
})();

(function(){
    if (statemachine.getPreviousStatusName(stm_document) == "Проект") {
        var currentEmployee = orgstructure.getCurrentEmployee();

        var resultList = signingWorkflow.createEmptyResultList(bpm_package, "Внутренний");
        signingWorkflow.createResultItem(resultList, currentEmployee);

        var taskDecision = signingWorkflow.getSignedDecision(currentEmployee);
        signingWorkflow.logDecision(resultList, taskDecision);

        signingWorkflow.deleteTempAssigneesList(execution);

        signingWorkflow.addSignBusinessJournalRecord(bpm_package, currentEmployee);
    }
})();

