//Зарегистрировать проект
(function(){
    var internal= stm_document;
    regnumbers.registerProject(internal, "INTERNAL_PRJ_NUMBER");
    internal.save();
})();

(function(){
    var document = stm_document;
    var fileRegister = document.assocs["lecm-eds-document:file-register-assoc"];
    var unit = null;
    if (fileRegister != null && fileRegister.length == 1) {
        unit = fileRegister[0].getParent().assocs["lecm-doc-dic-fr:organization-unit-assoc"][0];
    }
    if (unit == null) {
        unit = orgstructure.getPrimaryOrgUnit(orgstructure.getCurrentEmployee().nodeRef);
    }
    if(unit==null) unit = orgstructure.getMainOrganisationUnit();
    documentScript.finalizeToUnit(document, unit);
})();

var internal = stm_document;

var logText = "#initiator отправил(а) документ " + documentScript.wrapperDocumentLink(internal, "№ {~REGNUM}") + " на согласование";
logText = substitude.formatNodeTitle(internal.nodeRef.toString(), logText);
businessJournal.log(internal.nodeRef.toString(), "SEND_DOCUMENT", logText, []);

//уведомить о направлении на доработку
(function(){
    var internal = stm_document,
    author = internal.properties["lecm-document:author-assoc-ref"];

    var recipients = [];
    recipients.push(author);

    var notificationText = "Проект документа " + documentScript.wrapperDocumentLink(internal,"{lecm-document:present-string}") + " направлен Вам на доработку";      
    notifications.sendNotificationFromCurrentUser(recipients, notificationText, internal, true);
})();

(function(){
    var document = bpm_package.children[0];
    var fileRegister = document.assocs["lecm-eds-document:file-register-assoc"];
    var unit = null;
    if (fileRegister != null && fileRegister.length == 1) {
        unit = fileRegister[0].getParent().assocs["lecm-doc-dic-fr:organization-unit-assoc"][0];
    }

    if (unit == null) {
        unit = orgstructure.getPrimaryOrgUnit(orgstructure.getCurrentEmployee().nodeRef);
    }
	if(unit==null) unit = orgstructure.getMainOrganisationUnit();
    documentScript.finalizeToUnit(document, unit);
})();

//направить на регистрацию
(function(){
    var i,
        internal = stm_document, //исходящий документ
        registrars = edsGlobalSettings.getRegistrars('DA_REGISTRARS', internal),//получение списка регистраторов по документу
        registrarsCount = registrars.length,
        recipients = [],
        notificationText = 'Документ ' + documentScript.wrapperDocumentLink(internal, '{lecm-document:present-string}') + ' поступил Вам на регистрацию';

    //раздаем права регистраторам
    for(i = 0; i < registrarsCount; ++i) {
        statemachine.grandDynamicRoleForEmployee(internal, registrars[i], 'DA_REGISTRAR_DYN', task);
        recipients.push(registrars[i].getNodeRef().toString());
    }

    //уведомление
    notifications.sendNotificationFromCurrentUser(recipients, notificationText, internal, true);
})();

var internal = stm_document;

var logText = "#initiator направил(а) документ " + documentScript.wrapperDocumentLink(internal, "№ {~REGNUM}") + " на регистрацию";
logText = substitude.formatNodeTitle(internal.nodeRef.toString(), logText);
businessJournal.log(internal.nodeRef.toString(), "DIRECT_DOCUMENT", logText, []);

//Логирование перехода
(function(){
    var internal= stm_document;
    regnumbers.registerDocument(internal, "INTERNAL_DOC_NUMBER");
    internal.save();
    var logText = '#initiator перевел(а) документ #mainobject в статус "Зарегистрирован"';
    businessJournal.log(bpm_package.children[0].getNodeRef().toString(), "CHANGE_DOCUMENT_STATUS", logText, []);
})();

//
(function(){
    var senders = documentEvent.getEventSenders(stm_document);
    for each (var sender in senders) {
    if (sender.getTypeShort() == "lecm-internal-table-structure:answer") {
        var finalize = true;
        var answers = stm_document.assocs["lecm-internal-table-structure:answers-assoc"][0].children;
        for each (var answer in answers) {
            logger.error(answer.properties["lecm-internal-table-structure:answer-complete"]);
            finalize = finalize && answer.properties["lecm-internal-table-structure:answer-complete"];
        }
        if (finalize) {
            stm_document.properties["lecm-internal:all-answers-complete"] = true;
            stm_document.save();
        }
    } else {
        var answer = sender.sourceAssocs["lecm-internal-table-structure:answer-document-assoc"][0];
        if (answer != null && sender.properties["lecm-statemachine:status"] == "Направлен") {
            answer.properties["lecm-internal-table-structure:answer-complete"] = true;
            answer.properties["lecm-internal-table-structure:answer-details"] = "!html " + documentScript.wrapperAttribute(sender, sender.properties["lecm-document:title"]);
            answer.save();
        }
    }
    documentEvent.removeEventSender(stm_document, sender);
}
})();

//
(function(){
    var author = stm_document.assocs["lecm-document:author-assoc"][0];
    var primaryUnit = orgstructure.getPrimaryOrgUnit(author);
	if(primaryUnit==null) primaryUnit = orgstructure.getMainOrganisationUnit();
    var recipients = stm_document.assocs["lecm-eds-document:recipients-assoc"];
    var additionalUnits = [];
    for each (var recipient in recipients) {
        var unit = orgstructure.getPrimaryOrgUnit(recipient);
        additionalUnits.push(unit);
    }

    var isShared = stm_document.properties["lecm-document-aspects:is-shared-folder"];
    documentScript.finalizeToUnit(stm_document, isShared, primaryUnit, additionalUnits);

    // Выставляем права на ответы
    var answersTable = stm_document.assocs["lecm-internal-table-structure:answers-assoc"][0];
    var answers = answersTable.getChildren();
    for each (var answer in answers) {
        var employee = answer.assocs["lecm-internal-table-structure:employee-assoc"][0];
        var unit = orgstructure.getPrimaryOrgUnit(employee);
        if (primaryUnit != null) {
            var initiatorAuthority = orgstructure.getOrgstructureUnitAuthority(primaryUnit, isShared); 
            answer.setPermission("LECM_BASIC_PG_Reader", initiatorAuthority);
        }
        if (unit != null) {
            var recipientAuthority = orgstructure.getOrgstructureUnitAuthority(unit, isShared);
            answer.setPermission("LECM_BASIC_PG_Reader", recipientAuthority);
        }
    }
})();

//
(function(){
    if (statemachine.getPreviousStatusName(stm_document) == "Проект") {
        var currentEmployee = orgstructure.getCurrentEmployee();

        var resultList = signingWorkflow.createEmptyResultList(bpm_package, "Внутренний");
        signingWorkflow.createResultItem(resultList, currentEmployee);

        var taskDecision = signingWorkflow.getSignedDecision(currentEmployee);
        signingWorkflow.logDecision(resultList, taskDecision);

        signingWorkflow.deleteTempAssigneesList(execution);

        signingWorkflow.addSignBusinessJournalRecord(bpm_package, currentEmployee);
    }
})();

