item.property:
var meeting = document;
if (meeting !== null){
  var members = events.getEventMembers(meeting);
  /*сбросить имеющиеся ответы*/
  for(var i = 0; i < members.length; i++) {
    var row = events.getEmployeeEventMemberRow(meeting.nodeRef.toString(), members[i].nodeRef.toString());
    row.properties["lecm-events-ts:members-status"]="EMPTY";
    row.save();
  }
  /*разослать уведомления*/
  var text = "Необходимо согласовать время проведения мероприятия "+ events.wrapperEventLink(meeting, meeting.properties["lecm-document:present-string"]) +", председатель: {lecm-meetings:chairman-assoc/lecm-orgstr:employee-short-name}, время: {lecm-events:from-date?dd.MM.yyyy} - {lecm-events:from-date?HH:mm}";
  notifications.sendNotification(members, text, meeting);
}
