    routeWorkflow.startWorkflow(variables);

(function(){ /*создание листа согласования, обновление статуса участников для повторных */
  var document = stm_document;
  var isRepeated = !!stm_document.properties["lecm-events:is-repeated"];
  var DEFAULT_APPROVAL_TERM = routesGlobalSettings.getApprovalTerm();
  if(approvalRoutes.getState(document) == "UNDEF"){
		var workFolder = approvalRoutes.createEmptyWorkingFolder(document);

   		var iteration = approvalRoutes.createNewTemporaryNode(workFolder.getNodeRef(),"lecm-approval-route:route");
    	iteration.properties['lecm-approval-route:routeInterruptAfterReject'] = false;
    	iteration.properties['lecm-routes-v2:routeNotifyAboutEveryDecision'] = true;
    	iteration.properties['cm:title'] = 'нетиповой маршрут';
		iteration.save();
        iteration.removeAspect('sys:temporary');

		var stage = approvalRoutes.createNewTemporaryNode(iteration.getNodeRef(),"lecm-approval-route:stage");
		stage.properties['lecm-routes-v2:stageWorkflowType'] = "PARALLEL";
		stage.properties['lecm-routes-v2:stageWorkflowTerm'] = DEFAULT_APPROVAL_TERM;

    	var  day, month, year,
		approveDate = new Date();
		if (isRepeated) {
		  var documentDate = document.properties['lecm-events:from-date'];
		  approveDate.setDate(documentDate.getDate() - DEFAULT_APPROVAL_TERM);
		  approveDate.setMonth(documentDate.getMonth());
		  approveDate.setFullYear(documentDate.getYear() + 1900);
		}

		day = approveDate.getDate();
		day = day < 10 ? ("0" + day) : ("" + day);
		month = approveDate.getMonth() + 1;
		year = approveDate.getFullYear();

		stage.properties['cm:title'] = "Согласование повестки от " + day + "." + (month > 9 ? month : ("0" + month)) + "." + year;
		stage.save();
		stage.removeAspect('sys:temporary');

		var approvals = events.getEventMembers(document.nodeRef.toString());
		if (approvals && approvals.length) {
			for (i = 0, size = approvals.length; i < size; ++i) {
				if (isRepeated) {
				  var row = events.getEmployeeEventMemberRow(stm_document.nodeRef.toString(), approvals[i].nodeRef.toString());
				  row.properties["lecm-events-ts:members-status"] = "CONFIRMED";
				  row.save();
				}
				var stageItem = approvalRoutes.createNewTemporaryNode(stage.getNodeRef(),"lecm-approval-route:stageItem");
				stageItem.properties['lecm-base-aspects:order'] = i;
				stageItem.save();
				stageItem.createAssociation(approvals[i], 'lecm-routes-v2:stageItemMemberAssoc');
				stageItem.removeAspect('sys:temporary');
			}
		}

  }

})();

var document = stm_document;
if (document !== null) {
  var members = events.getEventMembers(document);
  for (var i = 0; i<members.length; i++) {
    documentMembers.addMemberWithoutCheckPermission(document, members[i], "LECM_BASIC_PG_Reader");
  }
}

(function(){
  var meeting = stm_document;
  meeting.properties["lecm-events:show-in-calendar"]=true;
  meeting.properties["lecm-events:send-notifications"]=true;
  meeting.save();
  var tableAssocs = meeting.assocs["lecm-meetings-ts:items-assoc"];
  if (tableAssocs!==null && tableAssocs.length > 0){
    var table = tableAssocs[0];
    var  itemAssocs = table.childAssocs["cm:contains"];
    if (itemAssocs!==null && itemAssocs.length > 0 ){
      /* Проверяем документы */
      for (var i=0; i<itemAssocs.length ;i++ ) {
		var item = itemAssocs[i];
        var documents = item.assocs["lecm-meetings-ts:documents-assoc"];
        if (documents!==null && documents.length>0) {
		for (var j=0; j<documents.length ;j++ ) {
			var document = documents[j];
			documentConnection.createConnection(meeting, document, 'forInformation', true );
		}
        }
      }
    }
  }
  /*Notify responsible for resources*/
  var resources = events.getEventResources(meeting);
  if (resources !== null && resources.length>0) {
    for (var k=0; k<resources.length ;k++ ) {
      var responsible = events.getResourceResponsible(resources[k]);
      if (responsible!==null) {
		  notifications.sendNotification({
		  recipients: responsible,
		  templateCode: 'MEETINGS_RESPONSIBLE_FOR_RESOURCES',
		  templateConfig: {
		    mainObject: meeting
		  }
		});
      }
    }
  }
  events.notifyMeeteengCreated(stm_document, true, false);
  /*create repeated*/
  meetings.createRepetedMeetings(stm_document);
}());
