(function(){
  var document = stm_document;
  var typeAssoc = document.assocs["lecm-eds-document:document-type-assoc"];
  if (typeAssoc) {
      var documentTypeDictionary = typeAssoc[0];
      document.properties["lecm-signing-v2-aspects:isESSigningRequired"] = documentTypeDictionary.properties["lecm-doc-dic-dt:es-sign-required"] === true;
      document.save();
  }
})();

/* направить на регистрацию */
(function () {
    var document = stm_document;
var registrationRequired = document.assocs["lecm-eds-document:document-type-assoc"][0].properties["lecm-doc-dic-dt:registration-required"];
	if (!registrationRequired) {
		var signingState = signingRoutes.getState(document);
		if (signingState) {
			document.properties["lecm-eds-aspect:change-status-to"] = "Подписан";
			document.save();
		} else {
			document.properties["lecm-eds-aspect:change-status-to"] = "Согласован";
			document.save();
		}
	} else {
		var autoRegistration = document.assocs["lecm-eds-document:document-type-assoc"][0].properties["lecm-doc-dic-dt:auto-registration"];
		if (autoRegistration) {
			var documentRegnum = document.properties["lecm-document:regnum"],
				projectNumber = document.properties["lecm-document-aspects:reg-project-data-number"],
				presentString = document.properties["lecm-document:present-string"],
				presentStringWithProjectNumber = presentString.replace(documentRegnum, projectNumber);

			lecmPermission.pushAuthentication();
			lecmPermission.setRunAsUserSystem();
			regnumbers.registerDocument(document, "ND_NUMBER");
			lecmPermission.popAuthentication();

			edsDocument.sendNotificationAndLogAboutAutoRegistration(document, presentStringWithProjectNumber);

			document.reset();
			document.properties["lecm-eds-aspect:change-status-to"] = "Зарегистрирован";
			document.save();
		} else {
			var dutyRegistrar = edsGlobalSettings.getDutyRegistrar();
			var registrars = [];
			if (edsGlobalSettings.isRegistrationCenralized()) {
				registrars =  orgstructure.getEmployeesByBusinessRoleId("DA_REGISTRARS", true);
				if (registrars && registrars.length) {
					for each (var registrar in registrars) {
						statemachine.grandDynamicRoleForEmployee(document, registrar, "DA_REGISTRAR_DYN", task);
					}
				} else {
					registrars.push(dutyRegistrar);
					statemachine.grandDynamicRoleForEmployee(document, dutyRegistrar, "DA_REGISTRAR_DYN", task);
				}
			} else {
				var primaryPosition = orgstructure.getPrimaryPosition(orgstructure.getCurrentEmployee().nodeRef.toString());
                if (primaryPosition) {
                    var unit = orgstructure.getUnitByStaff(primaryPosition.nodeRef.toString());
					if (unit) {
						registrars = edsGlobalSettings.getPotentialWorkers('DA_REGISTRARS', unit.nodeRef.toString(), true);
						while (!registrars || !registrars.length) {
							unit = orgstructure.getParentUnit(unit.nodeRef.toString(), false);
							if (unit) {
								registrars = edsGlobalSettings.getPotentialWorkers('DA_REGISTRARS', unit.nodeRef.toString(), true);
							} else {
								break;
							}
						}
					}
                }
				if (registrars && registrars.length) {
					for each (var registrar in registrars) {
						statemachine.grandDynamicRoleForEmployee(document, registrar, "DA_REGISTRAR_DYN", task);
					}
				} else {
					registrars.push(dutyRegistrar);
					statemachine.grandDynamicRoleForEmployee(document, dutyRegistrar, "DA_REGISTRAR_DYN", task);
				}
			}
			/* уведомление */
			notifications.sendNotificationFromCurrentUser({
				recipients: registrars,
				templateCode: 'ND_REGISTER',
				templateConfig: {
					mainObject: document
				},
				dontCheckAccessToObject: true
			});

			var logText = "#initiator направил(а) документ " + documentScript.wrapperDocumentLink(document, "№ {~REGNUM}") + " на регистрацию";
			logText = substitude.formatNodeTitle(document.nodeRef.toString(), logText);
			businessJournal.log(document.nodeRef.toString(), "DIRECT_DOCUMENT", logText, []);
		}
	}
})();

var nd = stm_document;

var logText = "#initiator отправил(а) документ " + documentScript.wrapperDocumentLink(nd, "№ {~REGNUM}") + " на согласование";
logText = substitude.formatNodeTitle(nd.nodeRef.toString(), logText);
businessJournal.log(nd.nodeRef.toString(), "SEND_DOCUMENT", logText, []);

var document = stm_document;

if (document) {
  var currentIterationState = signingRoutes.getState(document);
  if (currentIterationState != "NEW") {
   	var lastIteration = signingRoutes.getLastIteration(document);
    if (lastIteration) {
      	signingRoutes.createIterationFromPrevious(document);
    }
  }
  var variables = base.getValueConverter().convertValueForJava({
      workflowDefinition: 'lecmSigningWorkflow-v2',
      documentRef: document.nodeRef
  });

  routeWorkflow.startWorkflow(variables);
}

(function () {
	var document = stm_document;
  var registrationRequired = document.assocs["lecm-eds-document:document-type-assoc"][0].properties["lecm-doc-dic-dt:registration-required"];
	if (routesGlobalSettings.getSettings().properties["lecmRoutesGlobalSettings:autoRoutingAfterSigning"] && registrationRequired) {
		document.properties["lecm-eds-aspect:change-status-to"] = "На регистрации";
		document.save();
    }
}) ();

