item.property:
            if(!document.properties["lecm-os:nomenclature-case-no-permissions-change-on-archivation"]){
	operativeStorage.cleanVisibilityList(document.nodeRef);
	operativeStorage.grantAll(document.nodeRef);
}

document.properties["lecm-os:nomenclature-case-status"] = "ARCHIVE";

var childs = document.getChildAssocs();
for(var i = 0; i < childs.length; i++) {
	var child = childs[i];
	if(child && child.hasOwnProperty("lecm-statemachine:status")){
		child.properties["lecm-statemachine:status"] = "В архиве";
	}
}

operativeStorage.grantAccessToAllArchivhists(document.nodeRef);

document.save();
