var doc = bpm_package.children[0];
if (doc) {
    var childrenErrands = errands.getChildErrands(doc.nodeRef.toString());
    var hasOnWaitingExecution = false;
    if (childrenErrands && childrenErrands.length) {
        hasOnWaitingExecution = childrenErrands.some(function(child){
            return child.properties["lecm-statemachine:status"] == "Ожидает исполнения" || child.properties["lecm-statemachine:status"] == "На периодическом исполнении";
        });
    }
    doc.properties["lecm-ord:transit-to-on-execution"] = hasOnWaitingExecution;
    doc.properties["lecm-eds-aspect:child-change-signal-count"] = 0;
    doc.save();
}

(function(){
    var ord = stm_document;
    /*Регистрация*/
    regnumbers.registerDocument(ord, "ORD_NUMBER");
    ord.reset();
  /*Выставление флага "На контроле"*/
 	registratorAssocs = ord.assocs['lecm-ord:controller-assoc'];
	if (registratorAssocs && registratorAssocs.length){
		ord.properties["lecm-document-aspects:is-on-control"] = "true";
	}
    ord.save();
    /*Отмена документов*/
    var canceldDocAssoc = ord.assocs["lecm-ord:canceled-assoc"];
    if (canceldDocAssoc != null && canceldDocAssoc.length > 0) {
      for (var i = 0; i < canceldDocAssoc.length; i++) {
	  var cancelDoc = canceldDocAssoc[i];
      var cancelDocType = cancelDoc.getTypeShort();
      if (cancelDocType == "lecm-ord:document") {
      	    cancelDoc.properties["lecm-statemachine:status"] = "Отменен";
      } else if (cancelDocType == "lecm-nd:document") {
        var ndStatus = cancelDoc.properties["lecm-statemachine:status"];
        if (ndStatus == "Действует") {
          	cancelDoc.properties["lecm-statemachine:status"] = "Отменен";
        } else {
        	cancelDoc.addAspect("lecm-document-aspects:cancelled");
        }
      }
      cancelDoc.save();

      documentConnection.createConnection(ord, cancelDoc, "cancel", true, true);

      var mainDocDesc = substitude.formatNodeTitle(ord, documentScript.wrapperDocumentLink(ord, "{lecm-eds-document:document-type-assoc/cm:title}, № {~REGNUM} от {~REGDATE}"));
      var canceledDocDesc = substitude.formatNodeTitle(cancelDoc, documentScript.wrapperDocumentLink(cancelDoc, "{lecm-eds-document:document-type-assoc/cm:title}, № {~REGNUM} от {~REGDATE}"));

	  businessJournal.log(ord.nodeRef.toString(), "CANCEL_DOCUMENT", "Документ " + mainDocDesc + " отменил документ " + canceledDocDesc, []);
	  businessJournal.log(cancelDoc.nodeRef.toString(), "CANCEL_DOCUMENT",  "Документ " + canceledDocDesc + " отменен документом " + mainDocDesc, []);
	  }
    }
    /*Введение в действие*/
    var acceptedDocAssoc = ord.assocs["lecm-ord:accepted-assoc"];
    if (acceptedDocAssoc != null && acceptedDocAssoc.length > 0) {
      for (var ii = 0; ii < acceptedDocAssoc.length; ii++) {
      var acceptedDoc = acceptedDocAssoc[ii];
      acceptedDoc.addAspect("lecm-document-aspects:in-work");
      acceptedDoc.save();

      documentConnection.createConnection(ord, acceptedDoc, "accept", true, true);

      var mainDocDesc = substitude.formatNodeTitle(ord, documentScript.wrapperDocumentLink(ord, "№ {~REGNUM} от {~REGDATE}"));
      var acceptedDocDesc = substitude.formatNodeTitle(acceptedDoc, documentScript.wrapperDocumentLink(acceptedDoc, "№ {~REGNUM} от {~REGDATE}"));

	  businessJournal.log(ord.nodeRef.toString(), "ACCEPT_DOCUMENT", "Документ " + mainDocDesc + " ввел в действие документ " + acceptedDocDesc, []);
      businessJournal.log(acceptedDoc.nodeRef.toString(), "ACCEPT_DOCUMENT", "Документ " + acceptedDocDesc + " введен в действие документом " + mainDocDesc, []);
      }
    }
})();

