var doc = bpm_package.children[0];
if (doc) {
  	var ordControllerAssoc = doc.assocs["lecm-ord:controller-assoc"];
    var tableAssocs = doc.assocs["lecm-ord-table-structure:items-assoc"];
    if (tableAssocs && tableAssocs.length) {
        var table = tableAssocs[0];
        var pointAssocs = table.childAssocs["cm:contains"];
        if (pointAssocs && pointAssocs.length) {
            pointAssocs.forEach(function (point) {
                var errandAssoc = point.assocs["lecm-ord-table-structure:errand-assoc"];
                if (errandAssoc && errandAssoc.length) {
                    var pointStatusAssoc = point.assocs["lecm-ord-table-structure:item-status-assoc"];
                    if (pointStatusAssoc && pointStatusAssoc.length) {
                        var pointStatus = pointStatusAssoc[0].properties["cm:name"];
                        if (pointStatus == ordStatemachine.getPointStatusTextByCode("PERFORMANCE_STATUS")) {
                            var errand = errandAssoc[0];
                            var errandStatus = errand.properties["lecm-statemachine:status"];
                            if (errandStatus == "Исполнено" || errandStatus == "Отменено") {
                                var pointStatusCode = "";
                                if (errandStatus == "Исполнено") {
                                    pointStatusCode = "EXECUTED_STATUS";
                                } else if (errandStatus == "Отменено") {
                                    pointStatusCode = "CANCELED_STATUS";
                                }
                                ordStatemachine.changePointStatus(point.nodeRef.toString(), pointStatusCode);
                                pointStatus = ordStatemachine.getPointStatusTextByCode(pointStatusCode);
                                var currentUser = orgstructure.getCurrentEmployee();
                                var title = point.properties["lecm-ord-table-structure:title"];
                                var content = point.properties["lecm-ord-table-structure:item-content"];
                                var number = point.properties["lecm-document:indexTableRow"];
                                if (ordControllerAssoc && ordControllerAssoc.length) {
                                    notifications.sendNotificationFromCurrentUser({
                                        recipients: [ordControllerAssoc[0]],
                                        templateCode: 'ORD_ITEM_CHANGE_STATUS',
                                        templateConfig: {
                                            mainObject: doc,
                                            eventExecutor: currentUser,
                                            number: "Пункт номер " + number,
                                            numberTitle: title + " " + content,
                                            status: pointStatus,
                                            statusTitle: "",
                                            errand: errand
                                        },
                                        dontCheckAccessToObject: true
                                    });
                                }
                            }
                        }
                    }
                }
            });
        }
    }
  	var reviewState = doc.properties["lecm-review-ts:doc-review-state"];
  	var executionState = doc.properties["lecm-eds-aspect:execution-state"];
  	var confirmCompletion = doc.properties["lecm-ord:confirm-completion"];
  	var childErrands = errands.getChildErrands(doc.nodeRef.toString());
  	var haveActiveErrands = false;
    if (childErrands && childErrands.length) {
      haveActiveErrands = childErrands.some(function(childErrand){
      	return !statemachine.isFinal(childErrand.nodeRef.toString());
      });
    }
  	var activeReviewers = doc.properties["lecm-review-ts:active-reviewers"];
  	if (reviewState != "IN_PROCESS" && !activeReviewers && executionState != "IN_PROCESS" && !haveActiveErrands) {
      var ntCode = "ORD_COMPLETE";
      if (!confirmCompletion) {
        ntCode = "ORD_AUTO_COMPLETE";
        var logText = "ОРД:#mainobject";
        logText += " автоматически переведен в статус «Работа завершена» в связи с завершением работы по документу";
      	businessJournal.log(doc.nodeRef.toString(), "COMPLETE_DOCUMENT", logText, []);
        lecmPermission.pushAuthentication();
        lecmPermission.setRunAsUserSystem();
        doc.properties["lecm-ord:transit-to-work-completed"] = true;
        doc.save();
        lecmPermission.popAuthentication();
      }
      if (ordControllerAssoc && ordControllerAssoc.length) {
        notifications.sendNotificationFromCurrentUser({
          recipients: [ordControllerAssoc[0]],
          templateCode: ntCode,
          templateConfig: {
            mainObject: doc
          },
          dontCheckAccessToObject: true
        });
      }
    }
    edsDocument.resetChildChangeSignal(doc);
}

