(function () {
	var currentIterationNode = routesService.getDocumentCurrentIteration(stm_document),
		stageItems,
		employees,
		recipients = [],
		i,
		documentLink,
		msg;
	if (currentIterationNode) {
		stageItems = search.query({
			query: 'TYPE:"lecmWorkflowRoutes:stageItem" AND PATH:"' + currentIterationNode.qnamePath + '//*"',
			language: 'fts-alfresco'
		});
		for (i in stageItems) {
			employees = stageItems[i].assocs['lecmWorkflowRoutes:stageItemEmployeeAssoc'];
			if (employees && employees.length) {
				recipients.push(employees[0]);
			}
		}

		notifications.sendNotification({
            recipients: recipients,
            templateCode: 'ORD_REWORK',
            templateConfig: {
                mainObject: stm_document
            }
        });
	}
})();

(function() {
  var document = bpm_package.children[0];
  var currentEmployee = orgstructure.getCurrentEmployee();
  if (document) {
    var iterationNode = signingRoutes.createCompletedSigningRoute(document, [currentEmployee.nodeRef.toString()], "routes.title.signedByCompiler.route");
    var signerAssocs = document.assocs['lecm-signing-v2-aspects:signerEmployeeAssoc'];

    if (signerAssocs && signerAssocs.length) {
      signerAssocs.forEach(function(signer){
        document.removeAssociation(signer, 'lecm-signing-v2-aspects:signerEmployeeAssoc');
      });
    }
    document.createAssociation(currentEmployee, 'lecm-signing-v2-aspects:signerEmployeeAssoc');
    document.properties["lecm-signing-v2-aspects:isESSigningRequired"] = false;
    document.properties["lecm-signing-v2-aspects:isSigned"] = true;
    document.properties['lecm-signing-v2-aspects:signingState'] = 'COMPLETE';
    document.properties['lecm-signing-v2-aspects:isSigned'] = true;
    document.properties['lecm-signing-v2-aspects:signingDate'] = base.getValueConverter().convertValueForJava(new Date());
    document.save();
    var msg = 'Сотрудник #object1 подписал документ ' + documentScript.wrapperDocumentLink(document, '{lecm-document:ext-present-string}');
    businessJournal.log(document.nodeRef.toString(), 'SIGNING', substitude.formatNodeTitle(document.nodeRef.toString(), msg.toString()), [currentEmployee.nodeRef.toString()]);
  }
})();

var document = stm_document;
var jsonUtils = new Packages.org.springframework.extensions.webscripts.json.JSONUtils(),
        documentType = "" + document.typeShort, //для того чтобы java.lang.String превратился в js native string
        variables = null,
        workflowVariables = {
            workflowRole: "LECM_SIGNERS",
            workflowDynRole: "LECM_SIGNERS_DYN",
            workflowDefinition: "lecmSigningWorkflow-v2",
            documentRef: document.nodeRef
        };
    variables = jsonUtils.toJSONObject(workflowVariables);
    routeWorkflow.startWorkflow(variables);

(function () {
  var document = stm_document;
  var approvalDecision = approvalRoutes.getState(document);
  var registrationRequired = document.assocs["lecm-eds-document:document-type-assoc"][0].properties["lecm-doc-dic-dt:registration-required"];
	if (approvalDecision.contains("APPROVED") && document.properties["lecm-signing-v2-aspects:signed-on-paper"] && routesGlobalSettings.getSettings().properties["lecmRoutesGlobalSettings:autoRoutingAfterApproval"] && registrationRequired) {
		document.properties["lecm-eds-aspect:change-status-to"] = "На регистрации";
		document.save();
    }
}) ();

var doc = bpm_package.children[0];
if (doc) {
    var childrenErrands = errands.getChildErrands(doc.nodeRef.toString());
    var hasOnWaitingExecution = false;
    if (childrenErrands && childrenErrands.length) {
        hasOnWaitingExecution = childrenErrands.some(function(child){
            return child.properties["lecm-statemachine:status"] == "Ожидает исполнения";
        });
    }
    doc.properties["lecm-ord:transit-to-on-execution"] = hasOnWaitingExecution;
    doc.properties["lecm-eds-aspect:child-change-signal-count"] = 0;
    doc.save();
}

(function() {
  if (stm_document) {
    var controllerAssoc = stm_document.assocs["lecm-ord:controller-assoc"];
    if (controllerAssoc && controllerAssoc.length) {
      documentMembers.addMemberWithoutCheckPermission(stm_document, controllerAssoc[0], true);
    }
  }
})();

var document = bpm_package.children[0];
if (document) {
  var datesTable = document.assocs["lecm-ord-table-structure:items-assoc"][0];
  if (datesTable) {
    var datesTemp = document.assocs["lecm-ord:temp-items-assoc"];
    if (datesTemp && datesTemp.length) {
      datesTemp.forEach(function(item) {
        item.move(datesTable);
      });
    }
  }
}
