var i,
    ord = stm_document, //орд документ
    registrars = ordStatemachine.getRegistrars("DA_REGISTRAR", ord),//получение списка регистраторов по документу
    registrarsCount = registrars.length,
    notification;

//раздаем права регистраторам
for(i = 0; i < registrarsCount; ++i) {
    statemachine.grandDynamicRoleForEmployee(ord, registrars[i], "DA_REGISTRAR_DYNAMIC");
}

//подготавливаем уведомление
notification = ordStatemachine.prepareNotificationAboutRegistration(ord, registrars);

//рассылаем уведомление
notifications.sendNotification(notification);

var ord = stm_document;
var controllerAssoc = ord.assocs["lecm-ord:controller-assoc"];
if (controllerAssoc!=null && controllerAssoc.length > 0) {
    statemachine.executeTransitionAction(ord, "На исполнение");
}

var ord = stm_document;
var controllerAssoc = ord.assocs["lecm-ord:controller-assoc"];
if (controllerAssoc!=null && controllerAssoc.length > 0) {
  var controller = controllerAssoc[0];
  var tableAssocs = ord.assocs["lecm-ord-table-structure:items-assoc"];
  if (tableAssocs!=null && tableAssocs.length > 0){
     var table = tableAssocs[0];
     var  pointAssocs = table.childAssocs["cm:contains"]; 
     if (pointAssocs!=null && pointAssocs.length > 0 ){
        // формируем поручения
        ordStatemachine.formErrands(ord);
     }
  }

   // выдадим права контролеру
   statemachine.grandDynamicRoleForEmployee(ord, controller, "ORD_CONTROLLER_DYNAMIC");

   //подготавливаем уведомление 
   notification = ordStatemachine.prepareNotificationToController(ord, controller);

   //рассылаем уведомление
   notifications.sendNotification(notification);

