/* направить на регистрацию */
(function(){
    var i,
        outgoing = stm_document, /* исходящий документ */
        registrars, /* получение списка регистраторов по документу */
        registratorAssocs = outgoing.assocs['lecm-document-aspects:registrator-assoc'];
    if (registratorAssocs && registratorAssocs.length) {
        registrars = [delegation.getEffectiveEmployee(registratorAssocs[0], 'DA_REGISTRARS')];
    } else {
        registrars = edsGlobalSettings.getRegistrars("DA_REGISTRARS", outgoing);
    }
    /* раздаем права регистраторам */
    for(i = 0; i < registrars.length; ++i) {
        statemachine.grandDynamicRoleForEmployee(outgoing, registrars[i], "DA_REGISTRAR_DYN", task);
    }
    /* рассылаем уведомление */
    notifications.sendNotification({
        author: 'System',
        recipients: registrars,
        templateCode: 'OUTGOING_REGISTER',
        templateConfig: {
            mainObject: stm_document
        }
    });
})();

