businessJournal.log(outgoing.nodeRef.toString(), "DIRECT_DOCUMENT", logText, []);

/* Зарегистрировать документ */
(function() {
    var outgoing = stm_document;
    regnumbers.registerDocument(outgoing, "OUTGOING_DOC_NUMBER");
    outgoing.save();
})();

(function() {
	var document = stm_document,
		isReservationRunning = document.properties['lecm-reservation-aspects:isReservationRunning'],
		documentRegnum = document.properties["lecm-document:regnum"],
		projectNumber = document.properties["lecm-document-aspects:reg-project-data-number"],
		presentString = document.properties["lecm-document:present-string"],
		presentStringWithProjectNumber = presentString.replace(documentRegnum, projectNumber);

	if (isReservationRunning) {
		statemachine.terminateWorkflowsByDefinition(document, 'regnumReservationExecution', null, null);
	}
	var initiators = lecmPermission.getEmployeesByDynamicRole(document, "BR_INITIATOR");
    var eventInitiator = orgstructure.getCurrentEmployee();
    if (initiators && initiators.length) {
        notifications.sendNotification({
		  recipients: initiators,
		  templateCode: 'EDS_DOCUMENT_REGISTERED',
		  templateConfig: {
			  mainObject: document,
			  initiator: eventInitiator,
			  presentStringWithProjectNumber: presentStringWithProjectNumber
		  }
		});
    }

	var logText = "#initiator зарегистрировал документ " + documentScript.wrapperDocumentLink(document, presentStringWithProjectNumber) + ". Присвоен номер {~REGNUM} на дату {~REGDATE}.";
	logText = substitude.formatNodeTitle(document.nodeRef.toString(), logText);
	businessJournal.log(document.nodeRef.toString(), "REGISTRATION", logText, []);
})();

/* уведомить об отправке */
(function() {
    var outgoing = stm_document,
        senders = orgstructure.getEmployeesByBusinessRoleId('OUTGOING_SENDERS', true);
    notifications.sendNotification({
        author: 'System',
        recipients: senders,
        templateCode: 'OUTGOING_SEND',
        templateConfig: {
            mainObject: stm_document
        }
    });

	var logText = "#initiator направил(а) документ " + documentScript.wrapperDocumentLink(outgoing, "№ {~REGNUM} от {~REGDATE}") + " на отправку";
    logText = substitude.formatNodeTitle(outgoing.nodeRef.toString(), logText);
	businessJournal.log(outgoing.nodeRef.toString(), "DIRECT_DOCUMENT", logText, []);
})();

(function() {
	var document = bpm_package.children[0];
    operativeStorage.moveToNomenclatureCase(document);

    var executor = null;
	var executorAssoc = document.assocs["lecm-eds-document:executor-assoc"];
	if (executorAssoc && executorAssoc.length == 1) {
		executor = executorAssoc[0];
		documentMembers.addMemberWithoutCheckPermission(document, executor, "LECM_BASIC_PG_Reader", true);
	}
	notifications.sendNotification({
        recipients: [executor],
        templateCode: 'OUTGOING_SENT',
        templateConfig: {
            mainObject: stm_document,
			initiator: orgstructure.getCurrentEmployee()
        }
    });
})();

