(function() {
  var document = bpm_package.children[0];
  var currentEmployee = orgstructure.getCurrentEmployee();
  if (document) {
    var iterationNode = signingRoutes.createCompletedSigningRoute(document, [currentEmployee.nodeRef.toString()], "routes.title.signedByCompiler.route");
    var signerAssocs = document.assocs['lecm-signing-v2-aspects:signerEmployeeAssoc'];

    if (signerAssocs && signerAssocs.length) {
      signerAssocs.forEach(function(signer){
        document.removeAssociation(signer, 'lecm-signing-v2-aspects:signerEmployeeAssoc');
      });
    }
    document.createAssociation(currentEmployee, 'lecm-signing-v2-aspects:signerEmployeeAssoc');
    document.properties["lecm-signing-v2-aspects:isESSigningRequired"] = false;
    document.properties["lecm-signing-v2-aspects:isSigned"] = true;
    document.properties['lecm-signing-v2-aspects:signingState'] = 'COMPLETE';
    document.properties['lecm-signing-v2-aspects:isSigned'] = true;
    document.properties['lecm-signing-v2-aspects:signingDate'] = base.getValueConverter().convertValueForJava(new Date());
    document.save();
    var msg = 'Сотрудник #object1 подписал документ ' + documentScript.wrapperDocumentLink(document, '{lecm-document:ext-present-string}');
    businessJournal.log(document.nodeRef.toString(), 'SIGNING', substitude.formatNodeTitle(document.nodeRef.toString(), msg.toString()), [currentEmployee.nodeRef.toString()]);
  }
})();

