(function () {
	var currentIterationNode = routesService.getDocumentCurrentIteration(stm_document),
		stageItems,
		employees,
		recipients = [],
		i;
	if (currentIterationNode) {
		stageItems = search.query({
			query: 'TYPE:"lecmWorkflowRoutes:stageItem" AND PATH:"' + currentIterationNode.qnamePath + '//*"',
			language: 'fts-alfresco'
		});
		for (i in stageItems) {
			employees = stageItems[i].assocs['lecmWorkflowRoutes:stageItemEmployeeAssoc'];
			if (employees && employees.length) {
				recipients.push(employees[0]);
			}
		}
		notifications.sendNotification({
			recipients: recipients,
			templateCode: 'OUTGOING_REWORK',
			templateConfig: {
				mainObject: stm_document
			}
		});
	}
})();

