property.value:
(function(){
  var baseDoc = null;
  var baseDocAssoc = stm_document.assocs["lecm-resolutions:base-document-assoc"];
  if (baseDocAssoc && baseDocAssoc.length) {
    baseDoc = baseDocAssoc[0];
  }

  /* Автор */
  var creatorAssoc = stm_document.assocs["lecm-resolutions:author-assoc"];
  if (creatorAssoc && creatorAssoc.length) {
    statemachine.grandDynamicRoleForEmployee(stm_document, creatorAssoc[0], "RESOLUTION_AUTHOR_DYN", task);
    documentMembers.addMemberWithoutCheckPermission(stm_document, creatorAssoc[0], 'LECM_BASIC_PG_Reader', true);
    if (baseDoc) {
      documentMembers.addMemberWithoutCheckPermission(baseDoc, creatorAssoc[0], 'LECM_BASIC_PG_Reader', true);
    }
  }

  /* Создатель */
  var authorAssoc = stm_document.assocs["lecm-document:author-assoc"];
  if (authorAssoc && authorAssoc.length) {
    statemachine.grandDynamicRoleForEmployee(stm_document, authorAssoc[0], "RESOLUTION_CREATOR_DYN", task);
    documentMembers.addMemberWithoutCheckPermission(stm_document, authorAssoc[0], 'LECM_BASIC_PG_Reader', true);
    if (baseDoc) {
      documentMembers.addMemberWithoutCheckPermission(baseDoc, authorAssoc[0], 'LECM_BASIC_PG_Reader', true);
    }
  }

  /* Контролер */
  var controllerAssoc = stm_document.assocs["lecm-resolutions:controller-assoc"];
  if (controllerAssoc && controllerAssoc.length) {
    statemachine.grandDynamicRoleForEmployee(stm_document, controllerAssoc[0], "RESOLUTION_CONTROLLER_DYN", task);
    documentMembers.addMemberWithoutCheckPermission(stm_document, controllerAssoc[0], 'LECM_BASIC_PG_Reader', true);
    if (baseDoc) {
      documentMembers.addMemberWithoutCheckPermission(baseDoc, controllerAssoc[0], 'LECM_BASIC_PG_Reader', true);
    }

    notifications.sendNotificationFromCurrentUser({
      recipients: controllerAssoc,
      templateCode: 'RESOLUTION_ASSIGN_CONTROLLER',
      templateConfig: {
        mainObject: stm_document,
        eventExecutor: orgstructure.getCurrentEmployee()
      },
      dontCheckAccessToObject: true
    });
  }

  /* Завершающий */
  var closers = resolutionsScript.getResolutionClosers(stm_document);
  if (closers) {
    closers.forEach(function (employee) {
      statemachine.grandDynamicRoleForEmployee(stm_document, employee, "RESOLUTION_CLOSER_DYN", task);
    });
  }
})();

var document = stm_document;
if (document && !regnumbers.isDocumentRegistered(document)) {
  document.properties["lecm-resolutions:transition-to-execution-date"] = new Date();

  var executionDate = edsDocument.convertComplexDate(
    document.properties["lecm-resolutions:limitation-date-radio"],
    document.properties["lecm-resolutions:limitation-date"],
    document.properties["lecm-resolutions:limitation-date-type"],
    document.properties["lecm-resolutions:limitation-date-days"]);

  if (executionDate) {
    document.properties["lecm-resolutions:limitation-date-radio"] = "DATE";
    document.properties["lecm-resolutions:limitation-date"] = executionDate;
  }

  var regNumberCode;
  var parentDocAssoc = document.assocs["lecm-resolutions:base-assoc"];
  if (parentDocAssoc && parentDocAssoc.length) {
    regNumberCode = "RESOLUTION_ON_BASIS_NUMBER";

    var parentDocTypeShort = parentDocAssoc[0].typeShort;
    if (parentDocTypeShort == "lecm-incoming:document") {
      regNumberCode = "RESOLUTION_ON_INCOMING_BASIS_NUMBER";
    } else if (parentDocTypeShort == "lecm-internal:document") {
      regNumberCode = "RESOLUTION_ON_INTERNAL_BASIS_NUMBER";
    }

    var childIndex = 1;
    var childAssoc = parentDocAssoc[0].sourceAssocs["lecm-resolutions:base-assoc"];
    if (childAssoc && childAssoc.length) {
      childIndex += childAssoc.filter(function(child){
        return regnumbers.isDocumentRegistered(child);
      }).length;
    }
    document.properties["lecm-resolutions:child-index-counter"] = childIndex;
  } else {
    regNumberCode = "RESOLUTION_FREE_NUMBER";
  }
  document.save();

  regnumbers.registerDocument(document, regNumberCode);
  document.reset();
  document.properties["lecm-resolutions:number"] = document.properties["lecm-document:regnum"];
  document.save();

  if (document && document.properties["lecm-resolutions:errands-json"]) {
    var errandsList = eval('(' + document.properties["lecm-resolutions:errands-json"] + ')');
    if (errandsList && errandsList.length) {
      var i = 0;
      errandsList.forEach(function(errand) {
        errand["assoc_lecm-errands_additional-document-assoc"] = document.nodeRef.toString();
        errand["assoc_lecm-errands_initiator-assoc"] = document.properties["lecm-resolutions:author-assoc-ref"];
        i++;
        errand["prop_lecm-errands_child-index-counter"] = "" + i;
        errand["prop_lecm-errands_additional-doc-number"] = document.properties["lecm-resolutions:number"];
        errands.createErrands(errand);
      });
    }
  }
}

var document = stm_document;
if (document) {
  var baseDocAssoc = document.assocs["lecm-resolutions:base-assoc"];
  if (baseDocAssoc && baseDocAssoc.length) {
    edsDocument.sendChildChangeSignal(baseDocAssoc[0]);
  }
}

var document = stm_document;
if (document) {
  var annulReason = document.properties["lecm-resolutions:annul-signal-reason"];
  var recipients = [];

  var authorAssoc = document.assocs["lecm-resolutions:author-assoc"];
  if (authorAssoc && authorAssoc.length) {
    recipients.push(authorAssoc[0]);
  }

  var creatorAssoc = document.assocs["lecm-document:author-assoc"];
  if (creatorAssoc && creatorAssoc.length) {
    recipients.push(creatorAssoc[0]);
  }

  notifications.sendNotificationFromCurrentUser({
    recipients: recipients,
    templateCode: 'RESOLUTION_ANNUL',
    templateConfig: {
      mainObject: document,
      eventExecutor: orgstructure.getCurrentEmployee(),
      reason: annulReason
    },
    dontCheckAccessToObject: true
  });
}

