property.value:
var document = stm_document;
if (document) {
  var annulReason = document.properties["lecm-resolutions:annul-signal-reason"];

  var childErrands = document.sourceAssocs["lecm-errands:additional-document-assoc"];
  if (childErrands) {
    childErrands.forEach(function (errand) {
      errand.properties["lecm-errands:cancellation-signal"] = true;
      errand.save();
    });
  }

  var baseDocAssoc = document.assocs["lecm-resolutions:base-document-assoc"];
  if (baseDocAssoc && baseDocAssoc.length) {
    edsDocument.sendChildChangeSignal(baseDocAssoc[0]);
  }

  if (annulReason) {
    var recipients = [];
    var authorAssoc = document.assocs["lecm-resolutions:author-assoc"];
    if (authorAssoc && authorAssoc.length) {
      recipients.push(authorAssoc[0]);
    }
    var creatorAssoc = document.assocs["lecm-document:author-assoc"];
    if (creatorAssoc && creatorAssoc.length) {
      recipients.push(creatorAssoc[0]);
    }

    var controllerAssoc = document.assocs["lecm-resolutions:controller-assoc"];
    if (controllerAssoc && controllerAssoc.length) {
      recipients.push(controllerAssoc[0]);
    }
    notifications.sendNotificationFromCurrentUser({
      recipients: recipients,
      templateCode: 'RESOLUTION_ANNUL',
      templateConfig: {
        mainObject: document,
        eventExecutor: orgstructure.getCurrentEmployee(),
        reason: annulReason
      },
      dontCheckAccessToObject: true
    });
  }
}

var document = bpm_package.children[0];
if (document) {
  var childrenErrands = errands.getChildErrands(document.nodeRef.toString());
  if (childrenErrands && childrenErrands.length) {
    var canceledCount = 0;
    var executedCount = 0;

    childrenErrands.forEach(function (errand) {
      if ("Исполнено" == errand.properties["lecm-statemachine:status"]) {
        executedCount++;
      } else if ("Отменено" == errand.properties["lecm-statemachine:status"]) {
        canceledCount++;
      }
    });

    if (childrenErrands.length == (executedCount + canceledCount)) {
      if (childrenErrands.length == executedCount) {
        document.properties["lecm-resolutions:auto-complete"] = true;
      } if (childrenErrands.length == canceledCount) {
        document.properties["lecm-resolutions:annul-signal"] = true;
      } else {
        document.properties["lecm-resolutions:requires-solution-closers"] = true;

        notifications.sendNotificationFromCurrentUser({
          recipients: resolutionsScript.getResolutionClosers(document),
          templateCode: 'RESOLUTION_REQUIRES_SOLUTION_CLOSERS',
          templateConfig: {
            mainObject: document
          },
          dontCheckAccessToObject: true
        });
      }
    }
  }

  document.properties["lecm-eds-aspect:child-change-signal-count"] = 0;
  document.save();
}

var document = stm_document;
if (document && document.properties["lecm-resolutions:errands-json"]) {
  var errandsList = eval('(' + document.properties["lecm-resolutions:errands-json"] + ')');
  if (errandsList && errandsList.length) {
    var i = 0;
    errandsList.forEach(function(errand) {
      errand["assoc_lecm-errands_additional-document-assoc"] = document.nodeRef.toString();
      errand["assoc_lecm-errands_initiator-assoc"] = document.properties["lecm-resolutions:author-assoc-ref"];
      i++;
      errand["prop_lecm-errands_child-index-counter"] = "" + i;
      errands.createErrands(errand);
    });
  }
}

(function(){
  /* Автор */
  var creatorAssoc = stm_document.assocs["lecm-resolutions:author-assoc"];
  if (creatorAssoc && creatorAssoc.length) {
    statemachine.grandDynamicRoleForEmployee(stm_document, creatorAssoc[0], "RESOLUTION_AUTHOR_DYN", task);
  }

  /* Создатель */
  var authorAssoc = stm_document.assocs["lecm-document:author-assoc"];
  if (authorAssoc && authorAssoc.length) {
    statemachine.grandDynamicRoleForEmployee(stm_document, authorAssoc[0], "RESOLUTION_CREATOR_DYN", task);
  }

  /* Контролер */
  var controllerAssoc = stm_document.assocs["lecm-resolutions:controller-assoc"];
  if (controllerAssoc && controllerAssoc.length) {
    statemachine.grandDynamicRoleForEmployee(stm_document, controllerAssoc[0], "RESOLUTION_CONTROLLER_DYN", task);
  }

  /* Завершающий */
  var closers = resolutionsScript.getResolutionClosers(stm_document);
  if (closers) {
    closers.forEach(function (employee) {
      statemachine.grandDynamicRoleForEmployee(stm_document, employee, "RESOLUTION_CLOSER_DYN", task);
    });
  }
})();

