activiti:field.activiti:string:
(function(){ /* happy end */
  /* пометка итерации как активной */
  var currentIteration = execution.getVariableLocal('currentIteration');
  currentIteration.properties.lecmApproveAspects_approvalState = 'COMPLETE';
  currentIteration.save();
})();

(function(){ /*старт БП, подготовка переменных */
  var document = bpm_package.children[0];
  /* получение nodeRef-ы на текущую итерацию */
  var currentIteration = approval.getDocumentCurrentIteration(document);
  var routeInterruptAfterReject;
  var routeNotifyAboutEveryDecision;
  if (currentIteration) {
    /* пометка итерации как активной */
    currentIteration.addAspect('lecmApproveAspects:approvalDetailsAspect', {
      lecmApproveAspects_approvalState: 'ACTIVE'
    });
    execution.setVariableLocal('currentIteration', currentIteration);
    /* получение параметров из текущей итерации */
    routeInterruptAfterReject = currentIteration.properties.lecmWorkflowRoutes_routeInterruptAfterReject;
    routeNotifyAboutEveryDecision = currentIteration.properties.lecmWorkflowRoutes_routeNotifyAboutEveryDecision;
    execution.setVariable('routeInterruptAfterReject', routeInterruptAfterReject);
    execution.setVariable('routeNotifyAboutEveryDecision', routeNotifyAboutEveryDecision);
  } else {
    logger.error('The is no current iteration in document.');
  }
})();

(function(){ /* итератор по этапам */
  /* получение коллекции этапов */
  var currentIteration = execution.getVariableLocal('currentIteration');
  var stages = currentIteration.assocs.getChildAssocsByType('lecmWorkflowRoutes:stage');
  var stage, i, size, approvalState;
  for (i = 0, size = stages.length; i < size; ++i) {
    stage = stages[i];
    approvalState = stage.properties.lecmApproveAspects_approvalState;
    /* выбор первого неактивного этапа */
    if (!approvalState || 'NEW' == approvalState) {
      break;
    }
  }
  if (stage) {
    /* пометка этапа как активного */
    stage.addAspect('lecmApproveAspects:approvalDetailsAspect', {
      lecmApproveAspects_approvalState: 'ACTIVE'
    });
    /* передача этапа в подпроцесс */
    execution.setVariable('currentStage', stage);
  } else {
    logger.error('The is no any stage in current iteration');
  }
})();

(function(){ /* поиск еще незапущенных этапов */
/*
  установка флага hasNextStage, с учетом флага "завершать после первой визы "не согласен"
*/
  var stage = execution.getVariable('currentStage');
  var stageDecision = stage.properties.lecmApproveAspects_approvalDecision;
  execution.setVariableLocal('stageDecision', stageDecision);
  /* пометка этапа как завершенного */
  stage.properties.lecmApproveAspects_approvalState = 'COMPLETE';
  stage.save();
  /* получение коллекции этапов */
  var currentIteration = execution.getVariableLocal('currentIteration');
  var stages = currentIteration.assocs.getChildAssocsByType('lecmWorkflowRoutes:stage');
  var i, size, approvalState, hasNextStage;
  for (i = 0, size = stages.length; i < size; ++i) {
    approvalState = stages[i].properties.lecmApproveAspects_approvalState;
    /* поиск неактивных этапов */
    if (!approvalState || 'NEW' == approvalState) {
      hasNextStage = true;
      break;
    }
  }
  execution.setVariableLocal('hasNextStage', hasNextStage);
})();
